import React, { useState } from "react";

export default function GoldenTempleDetail({ onBack }) {
  const [activeTab, setActiveTab] = useState("HOTEL");
  const [selectedItem, setSelectedItem] = useState(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [showTiming, setShowTiming] = useState(false);
  const [ticketDetails, setTicketDetails] = useState({
    adults: 0,
    children: 0
  });

  const coorgData = {
    name: "Golden Temple (Namdroling Monastery), Coorg",
    description: "The second largest Tibetan settlement in India, known for its magnificent Buddhist monastery and golden statues",
    location: "Namdroling Monastery, Bylakuppe, Coorg, Karnataka 571104",
    mapUrl: "https://maps.google.com/?q=Golden+Temple+Coorg+Bylakuppe",
    timing: {
      Monday: "7:00 AM - 7:00 PM",
      Tuesday: "7:00 AM - 7:00 PM",
      Wednesday: "7:00 AM - 7:00 PM",
      Thursday: "7:00 AM - 7:00 PM",
      Friday: "7:00 AM - 7:00 PM",
      Saturday: "7:00 AM - 7:00 PM",
      Sunday: "7:00 AM - 7:00 PM"
    },
    ticketPrices: {
      adult: 0,
      child: 0
    },
    images: [
      "http://localhost:5000/images/golden_temple.jpg",
      "http://localhost:5000/images/golden-temple-2.jpg",
      "http://localhost:5000/images/golden-temple-3.jpg"
    ]
  };

  React.useEffect(() => {
    const slideInterval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % coorgData.images.length);
    }, 3000);
    return () => clearInterval(slideInterval);
  }, [coorgData.images.length]);

  const hotels = [
    {
      id: 1,
      type: "hotel",
      name: "Tibetan Heritage Hotel",
      contact: "+91-82736-61792",
      foodPrice: "₹600 - ₹1,800",
      address: "Bylakuppe, Near Golden Temple, Coorg",
      mapUrl: "https://maps.google.com/?q=Tibetan+Heritage+Hotel+Bylakuppe",
      image: "http://localhost:5000/images/tibetan-hotel.jpg",
      distance: "500 m from Golden Temple",
      rating: "4.2/5",
      amenities: ["Tibetan Decor", "Restaurant", "Prayer Room", "Free WiFi"]
    }
  ];

  const homestays = [
    {
      id: 1,
      type: "homestay",
      name: "Monastery View Homestay",
      contact: "+91-94481-12348",
      foodPrice: "₹350 - ₹700",
      address: "Bylakuppe Tibetan Settlement, Coorg",
      mapUrl: "https://maps.google.com/?q=Monastery+View+Homestay+Bylakuppe",
      image: "http://localhost:5000/images/monastery-homestay.jpg",
      distance: "1 km from Temple",
      rating: "4.0/5",
      amenities: ["Tibetan Culture", "Home Food", "Meditation", "Garden"]
    }
  ];

  const lodges = [
    {
      id: 1,
      type: "lodge",
      name: "Buddha Lodge",
      contact: "+91-97415-67893",
      foodPrice: "₹250 - ₹500",
      address: "Bylakuppe Main Road, Coorg",
      mapUrl: "https://maps.google.com/?q=Buddha+Lodge+Bylakuppe",
      image: "http://localhost:5000/images/buddha-lodge.jpg",
      distance: "800 m from Temple",
      rating: "3.9/5",
      amenities: ["Budget Stay", "Tibetan Food", "Basic Amenities"]
    }
  ];

  const foodStalls = [
    {
      id: 1,
      type: "food",
      name: "Tibetan Momo Stall",
      image: "http://localhost:5000/images/tibetan-momo.jpg",
      speciality: "Authentic Tibetan Momos & Thukpa"
    },
    {
      id: 2,
      type: "food",
      name: "Monastery Cafe",
      image: "http://localhost:5000/images/monastery-cafe.jpg",
      speciality: "Tibetan Butter Tea & Traditional Snacks"
    }
  ];

  // ... (Same component structure as previous files)
  // Include all the same functions and JSX structure
  return (
    <div style={{ position: "relative", textAlign: "center", padding: "20px", fontFamily: "Arial, sans-serif", maxWidth: "600px", margin: "0 auto" }}>
      {/* Same structure as ChikliholeDetail */}
    </div>
  );
}
