import React, { useState } from "react";

export default function NagarholeDetail({ onBack }) {
  const [activeTab, setActiveTab] = useState("HOTEL");
  const [selectedItem, setSelectedItem] = useState(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [showTiming, setShowTiming] = useState(false);
  const [ticketDetails, setTicketDetails] = useState({
    adults: 0,
    children: 0
  });

  const coorgData = {
    name: "Nagarhole National Park, Coorg",
    description: "A protected wildlife sanctuary known for its diverse flora and fauna, offering safari experiences",
    location: "Nagarhole National Park, Coorg, Karnataka 571201",
    mapUrl: "https://maps.google.com/?q=Nagarhole+National+Park+Coorg",
    timing: {
      Monday: "6:00 AM - 6:00 PM",
      Tuesday: "6:00 AM - 6:00 PM",
      Wednesday: "6:00 AM - 6:00 PM",
      Thursday: "6:00 AM - 6:00 PM",
      Friday: "6:00 AM - 6:00 PM",
      Saturday: "6:00 AM - 6:00 PM",
      Sunday: "6:00 AM - 6:00 PM"
    },
    ticketPrices: {
      adult: 250,
      child: 150
    },
    images: [
      "http://localhost:5000/images/nagarhole_national_park.jpg",
      "http://localhost:5000/images/nagarhole-2.jpg",
      "http://localhost:5000/images/nagarhole-3.jpg"
    ]
  };

  React.useEffect(() => {
    const slideInterval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % coorgData.images.length);
    }, 3000);
    return () => clearInterval(slideInterval);
  }, [coorgData.images.length]);

  const hotels = [
    {
      id: 1,
      type: "hotel",
      name: "Jungle Inn Resort",
      contact: "+91-82736-61791",
      foodPrice: "₹1,500 - ₹4,000",
      address: "Near Nagarhole National Park, Coorg",
      mapUrl: "https://maps.google.com/?q=Jungle+Inn+Resort+Coorg",
      image: "http://localhost:5000/images/jungle-inn.jpg",
      distance: "2 km from National Park",
      rating: "4.3/5",
      amenities: ["Safari Booking", "Pool", "Restaurant", "Wildlife Viewing"]
    }
  ];

  const homestays = [
    {
      id: 1,
      type: "homestay",
      name: "Wildlife Homestay",
      contact: "+91-94481-12347",
      foodPrice: "₹500 - ₹1,000",
      address: "Nagarhole Road, Coorg",
      mapUrl: "https://maps.google.com/?q=Wildlife+Homestay+Coorg",
      image: "http://localhost:5000/images/wildlife-homestay.jpg",
      distance: "3 km from Park",
      rating: "4.1/5",
      amenities: ["Safari Guide", "Home Food", "Nature Walk", "Bonfire"]
    }
  ];

  const lodges = [
    {
      id: 1,
      type: "lodge",
      name: "Forest Department Lodge",
      contact: "+91-97415-67892",
      foodPrice: "₹300 - ₹600",
      address: "Nagarhole National Park Entrance",
      mapUrl: "https://maps.google.com/?q=Forest+Department+Lodge+Coorg",
      image: "http://localhost:5000/images/forest-lodge-nagarhole.jpg",
      distance: "At Park Entrance",
      rating: "4.0/5",
      amenities: ["Official Lodge", "Safari Access", "Basic Amenities"]
    }
  ];

  const foodStalls = [
    {
      id: 1,
      type: "food",
      name: "Jungle Cafe",
      image: "http://localhost:5000/images/jungle-cafe.jpg",
      speciality: "Forest Special Veg & Non-Veg Meals"
    }
  ];

  // ... (rest of the component code same as ChikliholeDetail)
  // The structure is identical, just replace the content with Nagarhole specific data
}
