import React, { useState } from "react";

export default function MandalpattiDetail({ onBack }) {
  const [activeTab, setActiveTab] = useState("HOTEL");
  const [selectedItem, setSelectedItem] = useState(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [showTiming, setShowTiming] = useState(false);
  const [ticketDetails, setTicketDetails] = useState({
    adults: 0,
    children: 0
  });

  const coorgData = {
    name: "Mandalpatti Viewpoint, Coorg",
    description: "A unique vantage point overlooking the misty, rolling hills of the Pushpagiri forests, ideal for nature photography",
    location: "Mandalpatti Viewpoint, Pushpagiri Range, Coorg, Karnataka 571201",
    mapUrl: "https://maps.google.com/?q=Mandalpatti+Viewpoint+Coorg",
    timing: {
      Monday: "6:00 AM - 5:00 PM",
      Tuesday: "6:00 AM - 5:00 PM",
      Wednesday: "6:00 AM - 5:00 PM",
      Thursday: "6:00 AM - 5:00 PM",
      Friday: "6:00 AM - 5:00 PM",
      Saturday: "6:00 AM - 5:00 PM",
      Sunday: "6:00 AM - 5:00 PM"
    },
    ticketPrices: {
      adult: 150,
      child: 75
    },
    images: [
      "http://localhost:5000/images/mandalpatti_view.jpg",
      "http://localhost:5000/images/mandalpatti-2.jpg"
    ]
  };

  React.useEffect(() => {
    const slideInterval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % coorgData.images.length);
    }, 3000);
    return () => clearInterval(slideInterval);
  }, [coorgData.images.length]);

  const hotels = [
    {
      id: 1,
      type: "hotel",
      name: "Misty Hills Resort",
      contact: "+91-82736-61798",
      foodPrice: "â‚¹1,300 - â‚¹3,000",
      address: "Pushpagiri Range, Near Mandalpatti, Coorg",
      mapUrl: "https://maps.google.com/?q=Misty+Hills+Resort+Coorg",
      image: "http://localhost:5000/images/misty-hills-resort.jpg",
      distance: "4 km from Viewpoint",
      rating: "4.5/5",
      amenities: ["Valley View", "Photography Tours", "Restaurant", "Jeep Safari"]
    }
  ];

  const homestays = [
    {
      id: 1,
      type: "homestay",
      name: "Viewpoint Homestay",
      contact: "+91-94481-12354",
      foodPrice: "â‚¹450 - â‚¹850",
      address: "Mandalpatti Road, Coorg",
      mapUrl: "https://maps.google.com/?q=Viewpoint+Homestay+Coorg",
      image: "http://localhost:5000/images/viewpoint-homestay.jpg",
      distance: "2 km from Viewpoint",
      rating: "4.3/5",
      amenities: ["Mountain View", "Home Food", "Sunrise Viewing", "Bonfire"]
    }
  ];

  const lodges = [
    {
      id: 1,
      type: "lodge",
      name: "Summit Lodge",
      contact: "+91-97415-67899",
      foodPrice: "â‚¹350 - â‚¹650",
      address: "Mandalpatti Summit Area, Coorg",
      mapUrl: "https://maps.google.com/?q=Summit+Lodge+Coorg",
      image: "http://localhost:5000/images/summit-lodge.jpg",
      distance: "1 km from Viewpoint",
      rating: "4.0/5",
      amenities: ["Summit Access", "Basic Stay", "Viewing Deck"]
    }
  ];

  const foodStalls = [
    {
      id: 1,
      type: "food",
      name: "Misty Cafe",
      image: "http://localhost:5000/images/misty-cafe.jpg",
      speciality: "Hot Coffee & Mountain Snacks"
    },
    {
      id: 2,
      type: "food",
      name: "Viewpoint Food Stall",
      image: "http://localhost:5000/images/viewpoint-food.jpg",
      speciality: "Pakoras & Local Tea"
    }
  ];

  const handleItemClick = (item) => setSelectedItem(item);
  const handleBackToList = () => setSelectedItem(null);
  const handleFoodButtonClick = (item) => {
    alert(`Food Menu for ${item.name}\n\nðŸ½ï¸ Price Range: ${item.foodPrice}\nðŸ“ž Contact: ${item.contact}\nðŸ“ ${item.distance}`);
  };

  const handleTicketChange = (type, value) => {
    setTicketDetails(prev => ({
      ...prev,
      [type]: Math.max(0, value)
    }));
  };

  const calculateTotal = () => {
    return (ticketDetails.adults * coorgData.ticketPrices.adult) + 
           (ticketDetails.children * coorgData.ticketPrices.child);
  };

  if (selectedItem && selectedItem.type !== "food") {
    return (
      <div style={{ position: "relative", textAlign: "center", padding: "20px", fontFamily: "Arial, sans-serif", maxWidth: "600px", margin: "0 auto" }}>
        <button onClick={handleBackToList} style={{ position: "absolute", top: 12, left: 12, background: "#fff", border: "1px solid #ccc", borderRadius: 6, padding: "6px 10px", fontSize: 16, cursor: "pointer" }}>â† Back to List</button>
        <h2 style={{ marginTop: "40px", marginBottom: "20px" }}>{selectedItem.name}</h2>
        <div style={{ position: "relative" }}>
          <img src={selectedItem.image} alt={selectedItem.name} style={{ width: "100%", maxWidth: "400px", height: "200px", borderRadius: "5px", objectFit: "cover", marginBottom: "10px" }} onError={(e) => { e.currentTarget.onerror = null; e.currentTarget.src = "http://localhost:5000/images/default.jpg"; }} />
          <div style={{ position: "absolute", top: "10px", right: "10px", background: "rgba(255,255,255,0.9)", padding: "5px 10px", borderRadius: "20px", fontSize: "12px", fontWeight: "bold" }}>â­ {selectedItem.rating}</div>
        </div>
        <div style={{ textAlign: "left", backgroundColor: "#f9f9f9", padding: "20px", borderRadius: "5px", border: "1px solid #eee", marginBottom: "15px" }}>
          <h3 style={{ marginBottom: "15px" }}>{selectedItem.type.charAt(0).toUpperCase() + selectedItem.type.slice(1)} Information</h3>
          <div style={{ marginBottom: "10px" }}><strong>ðŸ“ž Contact:</strong> {selectedItem.contact}</div>
          <div style={{ marginBottom: "10px" }}><strong>ðŸ“ Address:</strong> {selectedItem.address}</div>
          <div style={{ marginBottom: "10px" }}><strong>ðŸ“ Distance:</strong> {selectedItem.distance}</div>
          <div style={{ marginBottom: "15px" }}><strong>ðŸ½ï¸ Food Price Range:</strong> {selectedItem.foodPrice}</div>
