import React, { useState, useEffect } from "react";
import PlaceGrid from "./components/PlaceGrid";
import PlaceDetail from "./components/PlaceDetail";
import { fetchPlaces } from "./api";

export default function App() {
  const [places, setPlaces] = useState([]);
  const [selected, setSelected] = useState(null);
  const [q, setQ] = useState("");
  const [currentSlide, setCurrentSlide] = useState(0);

  // Coorg places slideshow data
  const coorgSlides = [
    {
      id: 1,
      name: "Abbey Falls",
      image: process.env.PUBLIC_URL + "/images/abbey-falls.jpg",
      description: "Beautiful waterfall in the Brahmagiri Range"
    },
    {
      id: 2,
      name: "Raja's Seat",
      image: process.env.PUBLIC_URL + "/images/raja-seat.jpg",
      description: "Famous sunset viewpoint in Madikeri"
    },
    {
      id: 3,
      name: "Golden Temple",
      image: process.env.PUBLIC_URL + "/images/golden-temple.jpg",
      description: "Namdroling Monastery Buddhist temple"
    },
    {
      id: 4,
      name: "Coffee Plantation",
      image: process.env.PUBLIC_URL + "/images/coffee-plantation.jpg",
      description: "Lush coffee estates of Coorg"
    },
    {
      id: 5,
      name: "Tadiandamol Peak",
      image: process.env.PUBLIC_URL + "/images/tadiandamol.jpg",
      description: "Highest peak in Coorg with stunning views"
    }
  ];

  // Auto slide change every 3 seconds
  useEffect(() => {
    const slideInterval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % coorgSlides.length);
    }, 3000);

    return () => clearInterval(slideInterval);
  }, [coorgSlides.length]);

  // Load data from backend
  useEffect(() => {
    fetchPlaces().then(setPlaces).catch(() => setPlaces([]));
  }, []);

  // Filter places to avoid duplicates - only show unique places
  const uniquePlaces = Array.from(new Map(places.map(place => [place.name, place])).values());

  const filtered = uniquePlaces.filter((p) =>
    (p.name || "").toLowerCase().includes(q.toLowerCase())
  );

  return (
    <div style={{ 
      padding: "20px", 
      fontFamily: "Arial, sans-serif",
      minHeight: "100vh",
      overflowX: "hidden", // Prevent horizontal scroll
      backgroundColor: "#f5f5f5"
    }}>
      {/* Coorg Slideshow Section - Full Width */}
      <div style={{ 
        marginBottom: "30px",
        textAlign: "center",
        width: "100vw",
        marginLeft: "calc(-50vw + 50%)", // Center and extend to full width
        position: "relative",
        left: "50%",
        right: "50%"
      }}>
        <div style={{
          position: "relative",
          width: "100%",
          margin: "0 auto",
          overflow: "hidden",
          boxShadow: "0 4px 12px rgba(0,0,0,0.15)"
        }}>
          {/* Slide Image - Full Width */}
          <img
            src={coorgSlides[currentSlide].image}
            alt={coorgSlides[currentSlide].name}
            style={{
              width: "100%",
              height: "300px", // Increased height
              objectFit: "cover",
              display: "block" // Remove extra space below image
            }}
            onError={(e) => {
              e.currentTarget.src = process.env.PUBLIC_URL + "/images/default.jpg";
            }}
          />

          {/* Slide Overlay Text */}
          <div style={{
            position: "absolute",
            bottom: 0,
            left: 0,
            right: 0,
            background: "linear-gradient(transparent, rgba(0,0,0,0.8))",
            color: "white",
            padding: "25px 20px",
            textAlign: "left"
          }}>
            <h2 style={{ 
              margin: "0 0 8px 0",
              fontSize: "28px",
              fontWeight: "bold",
              textShadow: "2px 2px 4px rgba(0,0,0,0.5)"
            }}>
              {coorgSlides[currentSlide].name}
            </h2>
            <p style={{ 
              margin: 0,
              fontSize: "16px",
              opacity: 0.95,
              textShadow: "1px 1px 2px rgba(0,0,0,0.5)"
            }}>
              {coorgSlides[currentSlide].description}
            </p>
          </div>

          {/* Slide Indicators */}
          <div style={{
            position: "absolute",
            bottom: "20px",
            right: "20px",
            display: "flex",
            gap: "8px"
          }}>
            {coorgSlides.map((_, index) => (
              <button
                key={index}
                onClick={() => setCurrentSlide(index)}
                style={{
                  width: "12px",
                  height: "12px",
                  borderRadius: "50%",
                  border: "none",
                  backgroundColor: currentSlide === index ? "#fff" : "rgba(255,255,255,0.5)",
                  cursor: "pointer",
                  transition: "all 0.3s ease",
                  boxShadow: "0 2px 4px rgba(0,0,0,0.3)"
                }}
              />
            ))}
          </div>

          {/* Navigation Arrows */}
          <button
            onClick={() => setCurrentSlide((prev) => (prev - 1 + coorgSlides.length) % coorgSlides.length)}
            style={{
              position: "absolute",
              left: "20px",
              top: "50%",
              transform: "translateY(-50%)",
              background: "rgba(255,255,255,0.9)",
              border: "none",
              borderRadius: "50%",
              width: "50px",
              height: "50px",
              fontSize: "20px",
              cursor: "pointer",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              boxShadow: "0 2px 8px rgba(0,0,0,0.3)",
              transition: "all 0.3s ease"
            }}
            onMouseEnter={(e) => {
              e.target.style.background = "rgba(255,255,255,1)";
              e.target.style.transform = "translateY(-50%) scale(1.1)";
            }}
            onMouseLeave={(e) => {
              e.target.style.background = "rgba(255,255,255,0.9)";
              e.target.style.transform = "translateY(-50%) scale(1)";
            }}
          >
            ‹
          </button>
          <button
            onClick={() => setCurrentSlide((prev) => (prev + 1) % coorgSlides.length)}
            style={{
              position: "absolute",
              right: "20px",
              top: "50%",
              transform: "translateY(-50%)",
              background: "rgba(255,255,255,0.9)",
              border: "none",
              borderRadius: "50%",
              width: "50px",
              height: "50px",
              fontSize: "20px",
              cursor: "pointer",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              boxShadow: "0 2px 8px rgba(0,0,0,0.3)",
              transition: "all 0.3s ease"
            }}
            onMouseEnter={(e) => {
              e.target.style.background = "rgba(255,255,255,1)";
              e.target.style.transform = "translateY(-50%) scale(1.1)";
            }}
            onMouseLeave={(e) => {
              e.target.style.background = "rgba(255,255,255,0.9)";
              e.target.style.transform = "translateY(-50%) scale(1)";
            }}
          >
            ›
          </button>
        </div>
      </div>

      {/* Main Content Container */}
      <div style={{
        maxWidth: "1200px",
        margin: "0 auto",
        padding: "0 20px"
      }}>
        {!selected && (
          <>
            <div
              style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                marginBottom: "25px",
                gap: "15px"
              }}
            >
              <input
                value={q}
                onChange={(e) => setQ(e.target.value)}
                placeholder="Search places in Coorg..."
                style={{
                  flex: 1,
                  padding: "12px 16px",
                  borderRadius: "25px",
                  fontSize: "16px",
                  border: "2px solid #e0e0e0",
                  outline: "none",
                  transition: "all 0.3s ease"
                }}
                onFocus={(e) => {
                  e.target.style.borderColor = "#2c5530";
                  e.target.style.boxShadow = "0 0 0 3px rgba(44, 85, 48, 0.1)";
                }}
                onBlur={(e) => {
                  e.target.style.borderColor = "#e0e0e0";
                  e.target.style.boxShadow = "none";
                }}
              />
              <button
                style={{
                  padding: "12px 24px",
                  borderRadius: "25px",
                  fontSize: "16px",
                  backgroundColor: "#2c5530",
                  color: "white",
                  border: "none",
                  cursor: "pointer",
                  fontWeight: "bold",
                  transition: "all 0.3s ease",
                  whiteSpace: "nowrap"
                }}
                onMouseEnter={(e) => {
                  e.target.style.backgroundColor = "#1e3a1f";
                  e.target.style.transform = "translateY(-2px)";
                }}
                onMouseLeave={(e) => {
                  e.target.style.backgroundColor = "#2c5530";
                  e.target.style.transform = "translateY(0)";
                }}
              >
                Reviews
              </button>
            </div>

            <h3 style={{
              marginBottom: "20px",
              color: "#2c5530",
              fontSize: "24px",
              fontWeight: "bold",
              borderBottom: "2px solid #e0e0e0",
              paddingBottom: "10px"
            }}>
              List of Places in Coorg
            </h3>

            {filtered.length > 0 ? (
              <PlaceGrid places={filtered} onOpen={(p) => setSelected(p)} />
            ) : (
              <div style={{
                textAlign: "center",
                padding: "40px",
                color: "#666",
                fontSize: "18px"
              }}>
                {q ? `No places found for "${q}"` : "No places available"}
              </div>
            )}
          </>
        )}

        {selected && (
          <PlaceDetail place={selected} onBack={() => setSelected(null)} />
        )}
      </div>
    </div>
  );
}
