import React, { useState } from "react";
import PlaceGrid from "./components/PlaceGrid";

export default function App() {
  const [selected, setSelected] = useState(null);

  // 10 Places Using Public Images Folder
  const places = [
    { id: 1, name: "Beach Paradise", image: process.env.PUBLIC_URL + "/images/beach.jpg" },
    { id: 2, name: "Mountain View", image: process.env.PUBLIC_URL + "/images/mountain.jpg" },
    { id: 3, name: "Forest Escape", image: process.env.PUBLIC_URL + "/images/forest.jpg" },
    { id: 4, name: "City Lights", image: process.env.PUBLIC_URL + "/images/city.jpg" },
    { id: 5, name: "Waterfall Point", image: process.env.PUBLIC_URL + "/images/waterfall.jpg" },
    { id: 6, name: "Lake Paradise", image: process.env.PUBLIC_URL + "/images/lake.jpg" },
    { id: 7, name: "Temple View", image: process.env.PUBLIC_URL + "/images/temple.jpg" },
    { id: 8, name: "Desert Safari", image: process.env.PUBLIC_URL + "/images/desert.jpg" },
    { id: 9, name: "Island Resort", image: process.env.PUBLIC_URL + "/images/island.jpg" },
    { id: 10, name: "Snow Valley", image: process.env.PUBLIC_URL + "/images/snow.jpg" }
  ];

  return (
    <div>
      {!selected && (
        <PlaceGrid places={places} onOpen={(p) => setSelected(p)} />
      )}

      {selected && (
        <div style={{ padding: "20px" }}>
          <h2>{selected.name}</h2>
          <img
            src={selected.image}
            alt={selected.name}
            style={{ width: "300px", borderRadius: "10px", marginTop: "10px" }}
          />
          <br /><br />
          <button onClick={() => setSelected(null)}>Back</button>
        </div>
      )}
    </div>
  );
}
