import React, {useState, useEffect} from 'react'
import {fetchHotels, fetchFoods} from '../api'

export default function PlaceDetail({place, onBack}){
  const [tab, setTab] = useState('details')
  const [hotels, setHotels] = useState([])
  const [selectedHotel, setSelectedHotel] = useState(null)
  const [foods, setFoods] = useState([])

  useEffect(()=>{
    if(place){
      fetchHotels(place.id).then(setHotels).catch(()=>setHotels([]))
    }
  },[place])

  useEffect(()=>{
    if(selectedHotel) fetchFoods(selectedHotel.id).then(setFoods).catch(()=>setFoods([]))
  },[selectedHotel])

  return (
    <div className="place-detail">
      <button onClick={onBack}>Home</button>
      <div className="top">
        <img src={place.default_photo || 'https://via.placeholder.com/600x300'} alt={place.name} />
        <div className="right-controls">
          <button>Reviews</button>
          {place.lat && place.lng && (
            <iframe
              title="map"
              width="250"
              height="150"
              src={`https://www.google.com/maps?q=${place.lat},${place.lng}&z=15&output=embed`} />
          )}
        </div>
      </div>

      <div className="tabs">
        <button onClick={()=>setTab('details')}>Details</button>
        <button onClick={()=>setTab('hotels')}>Hotels</button>
        <button onClick={()=>setTab('lodges')}>Lodges</button>
        <button onClick={()=>setTab('homestays')}>Homestays</button>
      </div>

      <div className="tab-body">
        {tab === 'details' && (
          <div>
            <h2>{place.name}</h2>
            <p>{place.description}</p>
          </div>
        )}

        {(tab === 'hotels' || tab === 'lodges' || tab === 'homestays') && (
          <div>
            <h3>Nearby {tab}</h3>
            {hotels.filter(h => {
              if(tab === 'hotels') return h.type === 'hotel'
              if(tab === 'lodges') return h.type === 'lodge'
              return h.type === 'homestay'
            }).map(h => (
              <div key={h.id} className="hotel-item">
                <div className="hotel-title" onClick={()=>setSelectedHotel(h)}>near hotel is {h.name}</div>
              </div>
            ))}

            {selectedHotel && (
              <div className="food-list">
                <h4>Menu — {selectedHotel.name}</h4>
                {foods.length === 0 ? <p>No foods found</p> : (
                  <ul>{foods.map(f => <li key={f.id}>{f.name} — ₹{f.price}</li>)}</ul>
                )}
              </div>
            )}

          </div>
        )}
      </div>
    </div>
  )
}
