import React, { useState } from "react";

export default function IruppuFallsDetail({ onBack }) {
  const [activeTab, setActiveTab] = useState("HOTEL");
  const [selectedItem, setSelectedItem] = useState(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [showTiming, setShowTiming] = useState(false);
  const [ticketDetails, setTicketDetails] = useState({
    adults: 0,
    children: 0
  });

  const coorgData = {
    name: "Iruppu Falls, Coorg",
    description: "A stunning, spiritual waterfall also known as Lakshmana Tirtha Falls, located amidst dense forests",
    location: "Iruppu Falls, Brahmagiri Range, Coorg, Karnataka 571247",
    mapUrl: "https://maps.google.com/?q=Iruppu+Falls+Coorg",
    timing: {
      Monday: "6:00 AM - 6:00 PM",
      Tuesday: "6:00 AM - 6:00 PM",
      Wednesday: "6:00 AM - 6:00 PM",
      Thursday: "6:00 AM - 6:00 PM",
      Friday: "6:00 AM - 6:00 PM",
      Saturday: "6:00 AM - 6:00 PM",
      Sunday: "6:00 AM - 6:00 PM"
    },
    ticketPrices: {
      adult: 50,
      child: 25
    },
    images: [
      "http://localhost:5000/images/iruppu_falls.jpg",
      "http://localhost:5000/images/iruppu-falls-2.jpg",
      "http://localhost:5000/images/iruppu-falls-3.jpg"
    ]
  };

  React.useEffect(() => {
    const slideInterval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % coorgData.images.length);
    }, 3000);
    return () => clearInterval(slideInterval);
  }, [coorgData.images.length]);

  const hotels = [
    {
      id: 1,
      type: "hotel",
      name: "Iruppu Falls Resort",
      contact: "+91-82736-61796",
      foodPrice: "₹1,200 - ₹3,200",
      address: "Brahmagiri Range, Near Iruppu Falls, Coorg",
      mapUrl: "https://maps.google.com/?q=Iruppu+Falls+Resort+Coorg",
      image: "http://localhost:5000/images/iruppu-resort.jpg",
      distance: "3 km from Falls",
      rating: "4.3/5",
      amenities: ["Waterfall View", "Trekking", "Restaurant", "Nature Walk"]
    }
  ];

  // ... (Continue with homestays, lodges, foodStalls similar to previous components)
  return (
    <div style={{ position: "relative", textAlign: "center", padding: "20px", fontFamily: "Arial, sans-serif", maxWidth: "600px", margin: "0 auto" }}>
      {/* Same structure */}
    </div>
  );
}
