import React, { useState } from "react";

export default function DubareDetail({ onBack }) {
  const [activeTab, setActiveTab] = useState("HOTEL");
  const [selectedItem, setSelectedItem] = useState(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [showTiming, setShowTiming] = useState(false);
  const [ticketDetails, setTicketDetails] = useState({
    adults: 0,
    children: 0
  });

  const coorgData = {
    name: "Dubare Elephant Camp, Coorg",
    description: "An interactive camp where visitors can bathe and feed elephants under the guidance of naturalists",
    location: "Dubare Elephant Camp, Kushalnagar, Coorg, Karnataka 571234",
    mapUrl: "https://maps.google.com/?q=Dubare+Elephant+Camp+Coorg",
    timing: {
      Monday: "9:00 AM - 5:00 PM",
      Tuesday: "9:00 AM - 5:00 PM",
      Wednesday: "9:00 AM - 5:00 PM",
      Thursday: "9:00 AM - 5:00 PM",
      Friday: "9:00 AM - 5:00 PM",
      Saturday: "9:00 AM - 5:00 PM",
      Sunday: "9:00 AM - 5:00 PM"
    },
    ticketPrices: {
      adult: 1200,
      child: 800
    },
    images: [
      "http://localhost:5000/images/dubare_camp.jpg",
      "http://localhost:5000/images/dubare-2.jpg",
      "http://localhost:5000/images/dubare-3.jpg"
    ]
  };

  React.useEffect(() => {
    const slideInterval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % coorgData.images.length);
    }, 3000);
    return () => clearInterval(slideInterval);
  }, [coorgData.images.length]);

  const hotels = [
    {
      id: 1,
      type: "hotel",
      name: "Elephant Bay Resort",
      contact: "+91-82736-61793",
      foodPrice: "₹1,000 - ₹3,000",
      address: "Near Dubare Elephant Camp, Kushalnagar",
      mapUrl: "https://maps.google.com/?q=Elephant+Bay+Resort+Coorg",
      image: "http://localhost:5000/images/elephant-bay.jpg",
      distance: "2 km from Elephant Camp",
      rating: "4.4/5",
      amenities: ["River View", "Elephant Safari", "Pool", "Restaurant"]
    }
  ];

  const homestays = [
    {
      id: 1,
      type: "homestay",
      name: "Jungle Retreat Homestay",
      contact: "+91-94481-12349",
      foodPrice: "₹400 - ₹900",
      address: "Dubare Road, Kushalnagar, Coorg",
      mapUrl: "https://maps.google.com/?q=Jungle+Retreat+Homestay+Dubare",
      image: "http://localhost:5000/images/jungle-retreat.jpg",
      distance: "1.5 km from Camp",
      rating: "4.1/5",
      amenities: ["Elephant View", "Home Food", "Nature Walk", "River Access"]
    }
  ];

  const lodges = [
    {
      id: 1,
      type: "lodge",
      name: "Forest Camp Lodge",
      contact: "+91-97415-67894",
      foodPrice: "₹300 - ₹600",
      address: "Dubare Forest Area, Coorg",
      mapUrl: "https://maps.google.com/?q=Forest+Camp+Lodge+Dubare",
      image: "http://localhost:5000/images/forest-camp-lodge.jpg",
      distance: "At Camp Entrance",
      rating: "3.8/5",
      amenities: ["Elephant Interaction", "Basic Stay", "Jungle Experience"]
    }
  ];

  const foodStalls = [
    {
      id: 1,
      type: "food",
      name: "Elephant Camp Cafe",
      image: "http://localhost:5000/images/elephant-cafe.jpg",
      speciality: "Fresh Coconut Water & Local Snacks"
    }
  ];

  // ... (Same component structure)
  return (
    <div style={{ position: "relative", textAlign: "center", padding: "20px", fontFamily: "Arial, sans-serif", maxWidth: "600px", margin: "0 auto" }}>
      {/* Same structure */}
    </div>
  );
}
