import React from "react";

export default function PlaceCard({ place, onOpen }) {
  const getImageSrc = (p) => {
    const val = p?.default_photo || p?.image || "";
    if (!val) return "http://localhost:5000/images/default.jpg";
    if (/^https?:\/\//i.test(val)) return val;
    const cleaned = String(val).replace(/^\/+/, "");               // remove leading slash
    const replaced = cleaned.replace(/^uploads\//, "images/");    // adjust if backend stores under uploads
    return `http://localhost:5000/${replaced}`;
  };

  return (
    <div
      onClick={() => { console.log("open place:", place); onOpen(place); }}
      style={{
        border: "1px solid #ccc",
        padding: "20px",
        borderRadius: "10px",
        cursor: "pointer",
        textAlign: "center",
      }}
    >
      <img
        src={getImageSrc(place)}
        alt={place?.name || "Place"}
        style={{ width: "100%", height: "260px", objectFit: "cover", borderRadius: 8 }}
        onError={(e) => {
          e.currentTarget.onerror = null;
          e.currentTarget.src = "http://localhost:5000/images/default.jpg";
        }}
      />

      <h4>{place.name}</h4>
    </div>
  );
}
