import React from "react";

export default function PlaceDetail({ place, onBack }) {
  return (
    <div style={{ textAlign: "center", padding: "20px" }}>
      <h2>{place.name}</h2>

      <img
        src={place.image}
        alt={place.name}
        style={{ width: "300px", borderRadius: "10px" }}
      />

      <br /><br />

      <button onClick={onBack}>Back</button>
    </div>
  );
}
