            <button
              onClick={() => window.open(selectedHotel.mapUrl, '_blank')}
              style={{
                padding: "10px 20px",
                backgroundColor: "#4285f4",
                color: "white",
                border: "none",
                borderRadius: "4px",
                cursor: "pointer",
                fontSize: "14px"
              }}
            >
              View on Google Maps
            </button>

            <button
              onClick={() => handleFoodButtonClick(selectedHotel)}
              style={{
                padding: "10px 20px",
                backgroundColor: "#34a853",
                color: "white",
                border: "none",
                borderRadius: "4px",
                cursor: "pointer",
                fontSize: "14px"
              }}
            >
              Food Menu
